import API_ENDPOINT from"../../scripts/globals/api-endpoint";class RestaurantSource{static async getListRestaurant(){try{const t=await fetch(API_ENDPOINT.HOME),e=await t.json();if(e.error)throw new Error(e.message);return e.restaurants}catch(t){throw new Error(`Failed to fetch restaurant data: ${t.message}`)}}static async detailRestaurant(t){try{const e=await fetch(API_ENDPOINT.DETAIL(t)),r=await e.json();if(r.error)throw new Error(r.message);return r.restaurant}catch(t){throw new Error(`Failed to fetch restaurant detail: ${t.message}`)}}static async addReview(t){try{const e=await fetch(API_ENDPOINT.REVIEW,{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify(t)}),r=await e.json();if(!e.ok)throw new Error(r.message||"Failed to add review");return r.customerReviews}catch(t){throw new Error(`Failed to add review: ${t.message}`)}}}export default RestaurantSource;